// Basic text on the page
var info = document.createElement( 'div' );
info.style.position = 'absolute';
info.style.top = '10px';
info.style.width = '100%';
info.style.fontFamily = 'Monospace';
info.style.textAlign = 'center';
info.innerHTML = 'Click Havoc!, and let slip the derps of war';
document.body.appendChild( info );

// Create canvas thats the size of the entire window
var canvas = document.createElement( 'canvas' );
canvas.style.position = 'absolute';
canvas.width = window.innerWidth;
canvas.height = window.innerHeight;
document.body.appendChild( canvas );
console.log("canvas.width: " + canvas.width);
console.log("canvas.height: " + canvas.height);
var context = canvas.getContext( '2d' );

// Define our image size
var cwidth = 200, cwidthhalf = cwidth / 2;
var cheight = 200, cheighthalf = cheight / 2;

//Define our images
var source = [];
source.push("data:image/png;base64,");
source.push("data:image/png;base64,");
source.push("data:image/png;base64,");
source.push("data:image/png;base64,");
source.push("data:image/png;base64,");
source.push("data:image/png;base64,");
source.push("data:image/png;base64,");
source.push("data:image/png;base64,");
//source.push("data:image/png;base64,");

//Create usable objects from our images
var image = [];
var totalParticles = source.length;
console.log("init totalParticles: "+totalParticles)
for(var a = 0; a<totalParticles; a++){
	image[a] = document.createElement( 'img' );
	image[a].src = source[a];
}
var particles = [];

var Particle = function ( id, x, y, sx, sy ) {

	if ( sx === 0 ) sx = 2;

	var cx = ( id % 2 ) * cwidth;
	var cy = Math.floor( id / 2 ) * cheight;

	this.update = function () {

		x += sx;
		y += sy;

		if ( x < ( - cwidthhalf ) || x > ( canvas.width + cwidthhalf ) ) {
			// if particle is off the screen, drop drop array

			var index = particles.indexOf( this );
			particles.splice( index, 1 );
			return false;
		}

		if ( y > canvas.height - cheighthalf ) {
			y = canvas.height - cheighthalf;
			sy = - sy * 0.85;
		}

		sy += 0.98;

		context.drawImage( image[id], Math.floor( x - cwidthhalf ), Math.floor( y - cheighthalf ), cwidth, cheight	);

		return true;

	}

} 



var throwCard = function ( x, y ) {
	//Pick a random image and put it in the particle stack
	var randomNum = Math.floor((Math.random() * totalParticles));
	var particle = new Particle( randomNum, x, y, Math.floor( Math.random() * 6 - 3 ) * 2, - Math.random() * 16 );
	console.log("pushing particle #"+randomNum + " -- Current particle count: "+particles.length)
	particles.push( particle );
}

document.addEventListener( 'mousedown', function ( event ) {
	//Click? Particle!
	event.preventDefault();
	document.addEventListener( 'mousemove', onMouseMove, false );
}, false );

document.addEventListener( 'mouseup', function ( event ) {
	//Click? Particle!
	event.preventDefault();
	throwCard( event.clientX, event.clientY );
	document.removeEventListener( 'mousemove', onMouseMove, false );
}, false );

function onMouseMove( event ) {
	//Click & Move? Particle!
	event.preventDefault();
	throwCard( event.clientX, event.clientY );
}

document.addEventListener( 'touchstart', function ( event ) {
	//Touch is sexy? Particle!
	event.preventDefault();
	for ( var i = 0; i < event.changedTouches.length; i ++ ) {
		throwCard( event.changedTouches[ 0 ].pageX, event.changedTouches[ 0 ].pageY );
	}
}, false );

document.addEventListener( 'touchmove', function ( event ) {
	//Touch is sexy? Particle!
	event.preventDefault();
	for ( var i = 0; i < event.touches.length; i ++ ) {
		throwCard( event.touches[ i ].pageX, event.touches[ i ].pageY );
	}
}, false );

setInterval( function () {
	// Proccess through the particle stack
	var i = 0, l = particles.length;
	while ( i < l ) {
		particles[ i ].update() ? i ++ : l --;
	}
}, 1000 / 60 );
